public UIMenu AddSubMenu(UIMenu menu, string text, Point offset)
{
      var item = new UIMenuItem(text);
      menu.AddItem(item);
      var submenu = new UIMenu(menu.Title.Caption, text, offset);
      this.Add(submenu);
      menu.BindMenuToItem(submenu, item);
