```go
func (q Query) ForEach(c chan interface{}, action func(interface{})) {
	next := q.Iterate()
	if c == nil {
		for item, ok := next(); ok; item, ok = next() {
			action(item)
		}
	} else {
		var wg sync.WaitGroup
		for item, ok := next(); ok; item, ok = next() {
			wg.Add(1)
			go func(wg *sync.WaitGroup, item interface{}) {
				defer wg.Done()
				action(item)
			}(&wg, item)
		}

		go func() {
			wg.Wait()
			close(c)
		}()
	}
}
