            for (int a = i ; a < list.length; a++){

                if ( list[a].getCreateDate() > list[lowestDate].getCreateDate() ){
                    lowestDate = a;
                }
            }   
            orderedList.add(list[lowestDate]);
            list[lowestDate] = list[i];
        }
        return orderedList;
