``` java
import android.app.Application;
import main.java.riotapi.RiotApi;

public class GlobalClass extends Application {

    private RiotApi api;

    public GlobalClass() {
        this.api = new RiotApi("API-KEY");
    }

    public RiotApi getApi() {
        return api;
    }

}
```

