getAllByIndex<T>(storeName: string, indexName: string, keyRange: IDBKeyRange): Promise<T[]> {
    const data: T[] = [];
    return new Promise<T[]>((resolve, reject) => {
      from(this.dbService.openCursorByIndex(storeName, indexName, keyRange, (evt) => {
        const cursor: IDBCursorWithValue = (evt.target as IDBRequest<IDBCursorWithValue>).result;
        if (cursor) {
          data.push(cursor.value);
          cursor.continue();
        } else {
          resolve(data);
        }
      }));
    });
  }
```

