    public static string SanitizeXmlString(string xml) {
        if (xml == null) {
            return null;
        }

        System.Text.StringBuilder buffer = new System.Text.StringBuilder(xml.Length);

        foreach (char c in xml) {
            if (IsLegalXmlChar(c)) {
                buffer.Append(c);
            }
        }

