```csharp
            public static class Function1
    {
        [FunctionName("Function1")]
        public static async Task<IActionResult> Run(
            [HttpTrigger(AuthorizationLevel.Anonymous, "get", "post", Route = null)] HttpRequest req,
            ILogger log)
        {
            if (req.Method == "GET")
            {
                return new ObjectResult("Endpoint disabled");
            }

            var encodedSig = req.Headers["Signature"];
            var certPath = new Uri(req.Headers["SignatureCertChainUrl"], UriKind.Absolute);
            var body = string.Empty;
            using (StreamReader reader = new StreamReader(req.Body))
            {
                body = await reader.ReadToEndAsync();
            }

            if (!await RequestVerification.Verify(encodedSig, certPath, body))
            {
                return new BadRequestResult();
            }

            var request = JsonConvert.DeserializeObject<SkillRequest>(body);

            if (!RequestVerification.RequestTimestampWithinTolerance(request))
            {
                return new BadRequestResult();
            }

            var result = ResponseBuilder.Ask("did this work?", null);
            return new JsonResult(result);
        }
    }
```

